import React from 'react'
import Page from './Page'
import Section from "./Section";

const initialToDos = [
		{
			text: 'Überweisung für Nephrologie abholen',
			date: '2021-10-01',
			done: false
		},
		{
			text: 'Almutes Geburtstag',
			date: '2021-09-04T15:30:00',
			done: false
		},
		{
			text: 'Nachhilfe',
			date: '2021-09-01T18:30:00',
			done: false
		}
	];

class ToDoApp extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			tasks: initialToDos
		}
	}

	render() {
		return (
			<Page>
				<Section title="To Do">
					{this.state.tasks.length > 0 ? tasks.map(task => <h3>{task.text}</h3>) : "No tasks."}
				</Section>
			</Page>
		);
	}


}


export default ToDoApp;
