import React from 'react'
import {Button, Form, FormControl, Navbar} from 'react-bootstrap'
import CONFIGURATION from "./ConfigStore";

class MyNavbar extends React.Component {

	announceVersionUrl;
	updateParent;

	constructor(props) {
		super(props);
		this.announceVersionUrl = CONFIGURATION.baseUrl + "/{version}/announce";
		this.updateParent = props.update;
		this.state = {
			version: null
		}
	}

	announceVersion() {
		if (this.state.version != null) {
			fetch(this.announceVersionUrl.replace("{version}", this.state.version), {
				method: "POST"
			}).then(() => this.updateParent());
		}
	}

	render() {
		return (
			<Navbar bg="dark" variant="dark" expand="lg" sticky="top">
				<Navbar.Brand href="#home">Horizon Version Manager</Navbar.Brand>
				<Form inline>
					<FormControl type="text" placeholder="enter new version" className="mr-sm-2"
								 onChange={e => this.setState({version: e.target.value})}/>
					<Button variant="outline-success" onClick={() => this.announceVersion()}>Announce</Button>
				</Form>
			</Navbar>
		)
	}
}

export default MyNavbar;
