import React, {useState} from 'react'
import Page from './Page'
import Section from "./Section";
import Task from './Task'

const initialTasks = [
		{
			id: 1,
			beschreibung: 'Überweisung abholen',
			wichtig: true
		},
		{
			id: 2,
			beschreibung: 'Kuchen backen für Almutes Geburtstag',
			wichtig: true
		},
		{
			id: 3,
			beschreibung: 'Kaffee kaufen',
			wichtig: false
		}
	];

function ToDoApp() {
	const [tasks, setTasks] = useState(initialTasks)
	const toggleWichtig = (id) => {
		console.log(id)
		setTasks(tasks.map(task => task.id === id ? {...task, wichtig: !task.wichtig} : task))
	}
	return (
		<Page>
			<Section title="To Do">
				{tasks.length > 0 ? tasks.map(task => <Task key={task.id} task={task} wow={toggleWichtig}/>) : "No tasks."}
			</Section>
		</Page>
	);
}







export default ToDoApp;
